import React, { useEffect, useRef, useState } from 'react';
import { useHistory } from 'react-router-dom';
import { PageArea } from './styled';
import useApi from '../../helpers/OlxAPI';
import MaskedInput from 'react-text-mask';
import createNumberMask from 'text-mask-addons/dist/createNumberMask'

import { PageContainer, PageTitle, ErrorMessage } from '../../components/MainComponents';

const Page = () => {
    const api = useApi();
    const fileField = useRef();
    const history = useHistory();

    const [title, setTitle ] = useState('');
    const [category, setCategory ] = useState('');
    const [price, setPrice ] = useState('');
    const [priceNegociable , setPriceNegociable ] = useState(false);
    const [desc, setDesc ] = useState('');

    const [disabled, setDisabled] = useState(false);
    const [error, setError] = useState('');
    const [categories, setCategories] = useState ([]);

    useEffect(()=>{
    const getCategories = async ()=>{
        const cats = await api.getCategories();
        setCategories(cats)
        await console.log(categories)
    }
    getCategories();


    },[]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setDisabled(true);
        setError('');
        console.log('entrou no submit')
        let errors=[];

        if(!title.trim()){
            errors.push('Sem Titulo');
        }
        if(!category){
            errors.push("Sem Categoria")
        }

        if(error.length ===0){
            const fData = new FormData();
            fData.append('title', title);
            fData.append("price", price);
            fData.append('priceneg', priceNegociable);
            fData.append('desc',desc);
            fData.append('cat',category);
            console.log('Sem erro inicial')

            if(fileField.current.file.length > 0){
                for(let i=0; fileField.current.files.length; i++){
                    fData.append('img',fileField.current.files[i]);
                }
            }

            const json = await api.addAd(fData);

            if(!json.error){
                history.push(`/ad/${json.id}`);
                return
            }else{
                setError(json.error);
            }          


        }else{
            setError(errors.join('\n'));
        }

        setDisabled(false)



    }

    const priceMasc = createNumberMask({
        prefix:"R$ ",
        includeThousandsSeparator:true,
        thousandsSeparatorSymbol:'.',
        allowDecimal:true,
        decimalSymbol:',' 
    });

    return (
        <PageContainer>
            <PageTitle>Postar um anúncio</PageTitle>
            <PageArea>
                {error &&
                    <ErrorMessage>{error}</ErrorMessage>
                }

                <form onSubmit={handleSubmit}>
                    <label className="area">
                        <div className="area--title">Titulo</div>
                        <div className="area--input">
                            <input
                                type="text"
                                disabled={disabled}
                                value={title}
                                onChange={e=>setTitle(e.target.value)}
                                required
                            />
                        </div>
                    </label>
                    <label className="area">
                        <div className="area--title">Categoria</div>
                        <div className="area--input">
                            <select
                                disabled={disabled}
                                onChange={e=>setCategory(e.target.value)}
                                required
                            >
                                <option></option>
                                {categories && categories.map(i=>
                                    <option key={i._id} value={i._id}>{i.name}</option>
                                )}
                            </select>
                        </div>
                    </label>
                    <label className="area">
                        <div className="area--title">Preço</div>
                        <div className="area--input">
                           <MaskedInput
                                mask={priceMasc}
                                placeholder='R$ '
                                disabled={ disabled || priceNegociable}
                                value= {price}
                                onChange={e=>setPrice(e.target.value)}
                           />
                        </div>
                    </label>
                    <label className="area">
                        <div className="area--title">Preço Negociável</div>
                        <div className="area--input check">
                           <input 
                           type='checkbox'
                           disabled={disabled}
                           checked={priceNegociable}
                           onChange={e=>setPriceNegociable(!priceNegociable)}
                           />
                        </div>
                    </label>
                    <label className="area">
                        <div className="area--title">Decrição</div>
                        <div className="area--input">
                           <textarea
                                disabled={disabled}
                                value = {desc}
                                onChange={e=>setDesc(e.target.value)}
                           ></textarea>
                        </div>
                    </label>
                    <label className="area">
                        <div className="area--title">Imagens (1 ou mais)</div>
                        <div className="area--input">
                          <input
                            type='file'
                            disabled = {disabled}
                            ref={fileField}
                            multiple

                          />
                        </div>
                    </label>
                    <label className="area">
                        <div className="area--title"></div>
                        <div className="area--input">
                            <button disabled={disabled}>Adicionar anúncio</button>
                        </div>
                    </label>
                </form>
            </PageArea>
        </PageContainer>
    );
}

export default Page;
